const hgi_error = require('hgi-error')
const hgi_debug = require('hgi-debug')
const path = require('path');

module.exports.debugMiddleware = (req, res, next) => {
  return hgi_debug.debugMiddleware(req, res, next)
}

module.exports.notFoundMiddleware = (req, res, next) => {
  return hgi_error.notFoundMiddleware(req, res, next)
}

module.exports.errorMiddleware = (err, req, res, next) => {
  return hgi_error.errorMiddleware(err, req, res, next)
}

module.exports.testMiddleware = (req, res, next) => {
  return res.json({ name: path.basename(process.cwd()) })
}