const typeckecker = require('typechecker');

const types = {
    'any': () => true,
    'array': typeckecker.isArray,
    'boolean': typeckecker.isBoolean,
    'date': typeckecker.isDate,
    'function': typeckecker.isFunction,
    'number': typeckecker.isNumber,
    'object': typeckecker.isPlainObject,
    'regexp': typeckecker.isRegExp,
    'string': typeckecker.isString,
}

module.exports = types