import { RefObject, useEffect, useMemo, useRef } from 'react';

export const useConsolidatedRef = <T>(ref): RefObject<T> => {
  const localRef: RefObject<T> = useRef(null);

  const consolidatedRef = useMemo(() => {
    if (!ref || typeof ref === 'function') {
      return localRef;
    }
    return ref;
  }, [ref]);

  useEffect(() => {
    if (typeof ref === 'function') {
      // @ts-ignore
      ref(consolidatedRef.current);
    }
  }, [consolidatedRef.current]);

  return consolidatedRef;
};
import { getI18nBundleData } from '@ui5/webcomponents-base/dist/asset-registries/i18n';
import { fetchI18nBundle, getI18nBundle } from '@ui5/webcomponents-base/dist/i18nBundle';
import { attachLanguageChange, detachLanguageChange } from '@ui5/webcomponents-base/dist/locale/languageChange';
import { useIsomorphicLayoutEffect } from '@ui5/webcomponents-react-base/lib/hooks';
import { useEffect, useState } from 'react';
import { deprecationNotice } from '@ui5/webcomponents-react-base/lib/Utils';

type TextWithDefault = { key: string; defaultText: string } | string;
type TextWithPlaceholders = [TextWithDefault, ...string[]];

interface I18nBundle {
  getText: (textObj: TextWithDefault, ...args: any[]) => string;
}

const resolveTranslations = (bundle, texts) => {
  return texts.map((text) => {
    if (Array.isArray(text)) {
      const [key, ...replacements] = text;
      return bundle.getText(key, replacements);
    }
    return bundle.getText(text);
  });
};

export const useI18nText = (bundleName: string, ...texts: (TextWithDefault | TextWithPlaceholders)[]): string[] => {
  const i18nBundle: I18nBundle = getI18nBundle(bundleName);
  const [translations, setTranslations] = useState(resolveTranslations(i18nBundle, texts));

  useEffect(() => {
    deprecationNotice(
      'useI18nText',
      `'useI18nText' is deprecated and will be removed in the next release. Please use 'useI18nBundle' instead.
A Migration Guide can be found here: https://sap.github.io/ui5-webcomponents-react/?path=/docs/migration-guide--page#migrating-from-013x-to-0140`
    );
  }, []);

  useEffect(() => {
    let didCancel = false;
    const fetchAndLoadBundle = async () => {
      await fetchI18nBundle(bundleName);
      if (!didCancel) {
        setTranslations((prev) => {
          const next = resolveTranslations(i18nBundle, texts);
          if (prev.length === next.length && prev.every((translation, index) => next[index] === translation)) {
            return prev;
          }
          return next;
        });
      }
    };
    fetchAndLoadBundle();
    return () => {
      didCancel = true;
    };
  }, [fetchI18nBundle, bundleName, ...texts]);

  return translations;
};

export const useI18nBundle = (bundleName: string): I18nBundle => {
  const [_, setUpdater] = useState(0);

  useIsomorphicLayoutEffect(() => {
    let isMounted = true;
    const i18nBundleData = getI18nBundleData('@ui5/webcomponents-react');
    if (!i18nBundleData) {
      fetchI18nBundle(`${bundleName}`).then(() => {
        if (isMounted) {
          setUpdater((old) => old + 1);
        }
      });
    }
    return () => {
      isMounted = false;
    };
  }, [bundleName]);

  useEffect(() => {
    let isMounted = true;
    const handler = () => {
      fetchI18nBundle(`${bundleName}`).then(() => {
        if (isMounted) {
          setUpdater((old) => old + 1);
        }
      });
    };
    attachLanguageChange(handler);
    return () => {
      isMounted = false;
      detachLanguageChange(handler);
    };
  }, []);

  return getI18nBundle(bundleName);
};
import { useEffect, useLayoutEffect } from 'react';

export const useIsomorphicLayoutEffect = typeof window !== 'undefined' ? useLayoutEffect : useEffect;
const PROP_INCLUDELIST = /^(aria-|data-|id$|on[A-Z]|slot$)/;

export const usePassThroughHtmlProps = (props: Record<string, any>, propExcludeList: string[] = []) => {
  const componentPropExcludelist = new Set(propExcludeList);

  const returnVal: Record<string, unknown> = {};
  for (const name in props) {
    if (PROP_INCLUDELIST.test(name) && !componentPropExcludelist.has(name)) {
      returnVal[name] = props[name];
    }
  }

  return returnVal;
};
import { getCurrentRange, attachMediaHandler, detachMediaHandler } from '@ui5/webcomponents-react-base/lib/Device';
import { useEffect, useState } from 'react';

export const useViewportRange = (rangeSet: string) => {
  const [currentRange, setCurrentRange] = useState(getCurrentRange(rangeSet, window.innerWidth).name);

  useEffect(() => {
    let isMounted = true;
    const handler = ({ name: range }) => {
      if (isMounted === true) {
        setCurrentRange(range);
      }
    };
    attachMediaHandler(handler, rangeSet);
    return () => {
      isMounted = false;
      detachMediaHandler(handler);
    };
  }, [setCurrentRange]);

  return currentRange;
};
import { getRTL } from '@ui5/webcomponents-base/dist/config/RTL';
import { useIsomorphicLayoutEffect } from '@ui5/webcomponents-react-base/lib/hooks';
import { RefObject, useState } from 'react';

const GLOBAL_DIR_CSS_VAR = '--_ui5_dir';

const detectRTL = (elementRef: RefObject<HTMLElement>) => {
  const doc = window.document;
  const dirValues = ['ltr', 'rtl']; // exclude "auto" and "" from all calculations
  const locallyAppliedDir = getComputedStyle(elementRef.current).getPropertyValue(GLOBAL_DIR_CSS_VAR);

  // In that order, inspect the CSS Var (for modern browsers), the element itself, html and body (for IE fallback)
  if (dirValues.includes(locallyAppliedDir)) {
    return locallyAppliedDir === 'rtl';
  }
  if (dirValues.includes(elementRef.current?.dir)) {
    return elementRef.current?.dir === 'rtl';
  }
  if (dirValues.includes(doc.documentElement.dir)) {
    return doc.documentElement.dir === 'rtl';
  }
  if (dirValues.includes(doc.body.dir)) {
    return doc.body.dir === 'rtl';
  }

  // Finally, check the configuration for explicitly set RTL or language-implied RTL
  return getRTL();
};

const useIsRTL = (elementRef: RefObject<HTMLElement>): boolean => {
  const [isRTL, setRTL] = useState<boolean>(getRTL()); // use config RTL as best guess

  useIsomorphicLayoutEffect(() => {
    setRTL(detectRTL(elementRef)); // update immediately while rendering
    const targets = [document.documentElement, document.body, elementRef.current].filter(Boolean);
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.attributeName === 'dir') {
          setRTL(detectRTL(elementRef));
        }
      });
    });

    targets.forEach((target) => {
      observer.observe(target, { attributes: true, childList: false, characterData: false, attributeFilter: ['dir'] });
    });

    return () => {
      observer.disconnect();
    };
  }, []);

  return isRTL;
};

export { useIsRTL };
