import { registerThemeProperties } from "@ui5/webcomponents-base/dist/asset-registries/Themes.js";

import defaultThemeBase from "@ui5/webcomponents-theme-base/dist/generated/themes/sap_fiori_3/parameters-bundle.css.js";
import defaultTheme from "./sap_fiori_3/parameters-bundle.css.js";

registerThemeProperties("@ui5/webcomponents-theme-base", "sap_fiori_3", defaultThemeBase);
registerThemeProperties("@ui5/webcomponents", "sap_fiori_3", defaultTheme);
export default ".ui5-duration-picker-sliders-container{display:flex;justify-content:center;align-items:stretch;direction:ltr;padding:.5rem;min-width:18rem}.ui5-duration-picker-sliders-container.ui5-phone{height:90vh}.ui5-duration-picker-wheelslider{padding-left:.25rem;padding-right:.25rem}.ui5-duration-picker-footer{height:fit-content;display:flex;justify-content:flex-end;width:100%}.ui5-duration-picker-footer>[ui5-button]{margin:1%;min-width:20%}";
